pragma solidity 0.8.9;

interface IBondFactory {
    struct BondPrice {
        uint256 price;
        uint256 bid;
        uint256 ask;
        uint256 lastUpdated;
    }

    function priceFactor() external view returns (uint256);

    function getPrice(address bondToken_) external view returns (BondPrice memory price);
}

contracts/interfaces/IBond.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/IERC20Upgradeable.sol";

interface IBond is IERC20Upgradeable {
    function initialize(
        string memory name_,
        string memory symbol_,
        string memory series,
        address factory_,
        IERC20Upgradeable underlyingToken_,
        uint256 maturity_,
        string memory isin_
    ) external;

    function kind() external returns (string memory);

    function series() external returns (string memory);

    function isin() external returns (string memory);

    function underlyingOut(uint256 amount_, address to_) external;

    function grant(uint256 amount_) external;

    function faceValue(uint256 bondAmount_) external view returns (uint256);

    function amountToUnderlying(uint256 bondAmount_) external view returns (uint256);

    function emergencyWithdraw(
        IERC20Upgradeable token_,
        address to_,
        uint256 amount_
    ) external;
}

contracts/libs/Adminable.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.9;

abstract contract Adminable {
    event AdminUpdated(address indexed user, address indexed newAdmin);

    address public admin;

    modifier onlyAdmin() virtual {
        require(msg.sender == admin, "UNAUTHORIZED");

        _;
    }

    function setAdmin(address newAdmin) public virtual onlyAdmin {
        _setAdmin(newAdmin);
    }

    function _setAdmin(address newAdmin) internal {
        require(newAdmin != address(0), "Can not set admin to zero address");
        admin = newAdmin;

        emit AdminUpdated(msg.sender, newAdmin);
    }
}

