contract DTokenRegistry is Initializable, Adminable {

    address[] private dTokens;
    event DTokenCreated(address token);

    IController public appCtrl;
    address public feeConf;

    address public sharedVaultImplement;


    // --------------


    function dTokenList() view external returns (address[] memory) {
        return dTokens;
    }

    function initialize(
      address admin_,
      address appCtrl_,
      address feeConf_,
      address sharedVaultImplement_
    ) public initializer {
        require(admin_ != address(0), "Cant set admin to zero address");
        _setAdmin(admin_);

        appCtrl = IController(appCtrl_);
        feeConf = feeConf_;
        sharedVaultImplement = sharedVaultImplement_;
    }

    function updateAppCtrl(address contract_) external onlyAdmin {
      appCtrl = IController(contract_);
    }

    function updateFeeConf(address contract_) external onlyAdmin {
      feeConf = contract_;
    }


     // --------------


    function setSharedVaultImplement(address implement_) external onlyAdmin {
      sharedVaultImplement = implement_;
      for (uint256 i; i < dTokens.length; i++) {
          address token = dTokens[i];
          IUpgradable vault = IUpgradable(appCtrl.dyTokenVaults(token));
          vault.upgradeTo(sharedVaultImplement);
      }
    }

    function pitch() external view returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    ) {
      DTokenUSDOracle oracle = DTokenUSDOracle(0xb89aA7dF4EA99FEb54FDDF7f2269112ACFa680f5);
      AggregatorV3Interface ag = oracle.aggregators(0x5ACcEE91b9c4bd54a01316f2e7497659d98deB97) ;
      return ag.latestRoundData();
    }

    function touch(
      string memory name_,
      string memory symbol_,
      AggregatorV3Interface aggregator_
    ) external payable onlyAdmin returns (
      uint80 roundId,
      int256 answer,
      uint256 startedAt,
      uint256 updatedAt,
      uint80 answeredInRound
    ) {
      DToken token = new DToken(name_, symbol_);
      DTokenUSDOracle oracle = new DTokenUSDOracle();

      oracle.setAggregator(address(token), aggregator_);
      AggregatorV3Interface ag = oracle.aggregators(address(token));
      require(address(aggregator_) == address(ag));
      uint256 price = oracle.getPrice(address(token));
      require(price > 0, "price fucked up");
      return ag.latestRoundData();
    }

    function registerMintingDToken(
      string memory name_,
      string memory symbol_,
      IController.VaultState memory vstate_,
      AggregatorV3Interface aggregator_,
      uint16 collateralFactor_,
      uint16 creditBurn_
    ) external payable onlyAdmin {
      DToken token = new DToken(name_, symbol_);
      DTokenUSDOracle oracle = new DTokenUSDOracle();

      // MintVault_Proxy vault = new MintVault_Proxy(sharedVaultImplement); // TODO: unstable
      MintVault vault = new MintVault();
      vault.initialize(address(appCtrl), feeConf, address(token));

      oracle.setAggregator(address(token), aggregator_);
      uint256 price = oracle.getPrice(address(token));
      require(price > 0, "price fucked up");

      token.addMiner(address(vault));

      uint vtype = 2;  // vtype 1 : for deposit vault 2: for mint vault
      appCtrl.setVault(address(token), address(vault), vtype);
      appCtrl.setVaultStates(address(vault), vstate_);
      appCtrl.setOracles(address(token), address(oracle), collateralFactor_, creditBurn_);

      dTokens.push(address(token));
      emit DTokenCreated(address(token));
    }


}

