pragma solidity 0.8.9;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import "./DYTokenBase.sol";
import "./interfaces/IVault.sol";
import "./interfaces/IDepositVault.sol";

contract DYTokenERC20 is DYTokenBase {
    using SafeERC20 for IERC20;

    constructor(
        address _underlying,
        string memory _symbol,
        address _controller
    ) DYTokenBase(_underlying, _symbol, _controller) {}

    function depositCoin(address _to, address _toVault) public payable override {
        revert("DO_NOT_DEPOSIT_COIN");
    }

    function deposit(uint256 _amount, address _toVault) external override {
        depositTo(msg.sender, _amount, _toVault);
    }

    function depositTo(
        address _to,
        uint256 _amount,
        address _toVault
    ) public override {
        uint256 total = underlyingTotal();
        IERC20 underlyingToken = IERC20(underlying);

        uint256 before = underlyingToken.balanceOf(address(this));
        underlyingToken.safeTransferFrom(msg.sender, address(this), _amount);
        uint256 realAmount = underlyingToken.balanceOf(address(this)) - before;
        // Additional check for deflationary tokens
        require(realAmount >= _amount, "illegal amount");

        uint256 shares = 0;
        if (totalSupply() == 0) {
            require(_amount >= 10000, "too small");
            // lock the first MINIMUM_SUPPLY tokens to controller
            _mint(controller, MINIMUM_SUPPLY);
            shares = _amount;
        } else {
            shares = (_amount * totalSupply()) / total;
        }

        require(shares > 0, "ZERO_SHARE");
        //
        if (_toVault != address(0)) {
            require(_toVault == IController(controller).dyTokenVaults(address(this)), "mismatch dToken vault");
            _mint(_toVault, shares);
            IDepositVault(_toVault).syncDeposit(address(this), shares, _to);
        } else {
            _mint(_to, shares);
        }

        earn();
    }

    function withdraw(
        address _to,
        uint256 _shares,
        bool
    ) public override {
        require(_shares > 0, "shares need > 0");
        require(totalSupply() > 0, "no deposit");

        uint256 r = (underlyingTotal() * _shares) / totalSupply();
        _burn(msg.sender, _shares);

        uint256 b = IERC20(underlying).balanceOf(address(this));
        // need withdraw from strategy
        if (b < r) {
            uint256 withdrawAmount = r - b;

            address strategy = IController(controller).strategies(underlying);
            if (strategy != address(0)) {
                IStrategy(strategy).withdraw(withdrawAmount);
            }

            uint256 withdrawed = IERC20(underlying).balanceOf(address(this)) - b;
            if (withdrawed < withdrawAmount) {
                r = b + withdrawed;
            }
        }

        IERC20(underlying).safeTransfer(_to, r);
    }

    function earn() public override {
        uint256 b = IERC20(underlying).balanceOf(address(this));

        address strategy = IController(controller).strategies(underlying);
        if (strategy != address(0)) {
            IERC20(underlying).safeTransfer(strategy, b);
            IStrategy(strategy).deposit();
        }
    }
}
","keccak256":"0x737cb26edcacf68ca6db1b06ae107480c6451ee5fd0922c38aeb60f0ce3a4925","license":"MIT"},"contracts/VaultFactory.sol":{"content":"import "@openzeppelin/contracts/proxy/transparent/TransparentUpgradeableProxy.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";

import "./DYTokenERC20.sol";
import "./interfaces/IUSDOracle.sol";
import "./interfaces/IDepositVaultInitializer.sol";
import "./libs/Adminable.sol";
import "./interfaces/IController.sol";

contract VaultFactory is Initializable, Adminable {
    IController public controller;
    address public feeConf;
    address[] public vaults;
    mapping(string => address) public vaultImplementations;
    string[] public vaultKinds;
    address public vaultProxyAdmin;

    event VaultCreated(address indexed vault);

    constructor() initializer {}

    function initialize(
        IController controller_,
        address feeConf_,
        address admin_,
        address vaultProxyAdmin_
    ) external initializer {
        controller = controller_;
        feeConf = feeConf_;
        _setAdmin(admin_);
        vaultProxyAdmin = vaultProxyAdmin_;
    }

    function setController(IController controller_) external onlyAdmin {
        controller = controller_;
    }

    function setVaultProxyAdmin(address vaultProxyAdmin_) external onlyAdmin {
        vaultProxyAdmin = vaultProxyAdmin_;
    }

    function setFeeConf(address feeConf_) external onlyAdmin {
        feeConf = feeConf_;
    }

    function setVaultImplementation(string calldata kind_, address impl_) external onlyAdmin {
        if (vaultImplementations[kind_] == address(0)) {
            vaultKinds.push(kind_);
        }
        vaultImplementations[kind_] = impl_;
    }

    function createDepositVault(
        IERC20Metadata underlying_,
        IUSDOracle oracle_,
        uint16 discount_,
        uint16 premium_,
        string calldata kind_
    ) external onlyAdmin returns (address vaultAddress) {
        DYTokenERC20 dyToken = new DYTokenERC20(address(underlying_), underlying_.symbol(), address(controller));
        address dyTokenAddress = address(dyToken);
        controller.setDYToken(address(underlying_), dyTokenAddress);
        address vaultImpl = vaultImplementations[kind_];
        require(vaultImpl != address(0), "Invalid vault kind");
        bytes memory proxyData;
        require(vaultProxyAdmin != address(0), "invalid vault proxy admin");
        TransparentUpgradeableProxy proxy = new TransparentUpgradeableProxy(vaultImpl, vaultProxyAdmin, proxyData);
        vaultAddress = address(proxy);
        IDepositVaultInitializer vault = IDepositVaultInitializer(vaultAddress);
        vault.initialize(address(controller), feeConf, dyTokenAddress);
        controller.setVault(dyTokenAddress, vaultAddress, 1);
        _initValidVault(vaultAddress);
        controller.setOracles(address(underlying_), address(oracle_), discount_, premium_);
        controller.setVaultStates(
            vaultAddress,
            IController.VaultState({
                enabled: true,
                enableDeposit: true,
                enableWithdraw: true,
                enableBorrow: false,
                enableRepay: false,
                enableLiquidate: true
            })
        );
        vault.transferOwnership(msg.sender);
        vaults.push(vaultAddress);
        emit VaultCreated(vaultAddress);
    }

    function _initValidVault(address vaultAddress) internal {
        IController.ValidVault[] memory validVault = new IController.ValidVault[](1);
        validVault[0] = IController.ValidVault.No;
        address[] memory validVaultAddress = new address[](1);
        validVaultAddress[0] = vaultAddress;
        // can not be collateralized by default, enable manually if needed
        controller.initValidVault(validVaultAddress, validVault);
    }
}
","keccak256":"0x289f096dd75bea1be2d985d4e2c456e2be7e3053606c7cce71e6b616253b7c2a"},"contracts/interfaces/IController.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

interface IController {
    // manage Vault state for risk control
    struct VaultState {
        bool enabled;
        bool enableDeposit;
        bool enableWithdraw;
        bool enableBorrow;
        bool enableRepay;
        bool enableLiquidate;
    }

    function dyTokens(address) external view returns (address);

    function getValueConf(address _underlying)
        external
        view
        returns (
            address oracle,
            uint16 dr,
            uint16 pr
        );

    function getValueConfs(address token0, address token1)
        external
        view
        returns (
            address oracle0,
            uint16 dr0,
            uint16 pr0,
            address oracle1,
            uint16 dr1,
            uint16 pr1
        );

    function strategies(address) external view returns (address);

    function dyTokenVaults(address) external view returns (address);

    function beforeDeposit(
        address,
        address _vault,
        uint256
    ) external view;

    function beforeBorrow(
        address _borrower,
        address _vault,
        uint256 _amount
    ) external view;

    function beforeWithdraw(
        address _redeemer,
        address _vault,
        uint256 _amount
    ) external view;

    function beforeRepay(
        address _repayer,
        address _vault,
        uint256 _amount
    ) external view;

    function joinVault(address _user, bool isDeposit) external;

    function exitVault(address _user, bool isDeposit) external;

    function userValues(address _user, bool _dp)
        external
        view
        returns (uint256 totalDepositValue, uint256 totalBorrowValue);

    function userTotalValues(address _user, bool _dp)
        external
        view
        returns (uint256 totalDepositValue, uint256 totalBorrowValue);

    function liquidate(address _borrower, bytes calldata data) external;

    // ValidVault 0: uninitialized, default value
    // ValidVault 1: No, vault can not be collateralized
    // ValidVault 2: Yes, vault can be collateralized
    enum ValidVault {
        UnInit,
        No,
        Yes
    }

    function initValidVault(address[] memory _vault, ValidVault[] memory _state) external;

    function validVaults(address _vault) external view returns (ValidVault);

    function validVaultsOfUser(address _vault, address _user) external view returns (ValidVault);

    function setDYToken(address _underlying, address _dToken) external;

    function setVault(
        address _dyToken,
        address _vault,
        uint256 vtype
    ) external;

    function setOracles(
        address _underlying,
        address _oracle,
        uint16 _discount,
        uint16 _premium
    ) external;

    function setVaultStates(address _vault, VaultState memory _state) external;
}
